import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import api from '../api';
import AuthCard from '../components/AuthCard';


export default function Home(){
const [city, setCity] = useState('');
const [name, setName] = useState('');
const [email, setEmail] = useState('');
const [contact, setContact] = useState('');
const [gender, setGender] = useState('male');
const [password, setPassword] = useState('');
const [roleReg, setRoleReg] = useState('user');
const [error, setError] = useState('');

// Login side
const [emailLogin, setEmailLogin] = useState('');
const [passwordLogin, setPasswordLogin] = useState('');
const [roleLogin, setRoleLogin] = useState('user');
const [errorLogin, setErrorLogin] = useState('');
const navigate = useNavigate();
const onSearch = (e)=>{
e.preventDefault();
// move to /search?city=name
navigate(`/search?city=${encodeURIComponent(city)}`);
}
return (
<main className="container">
<section className="hero">
<h2>Welcome to PG Connect</h2>
<div style={{ display:'grid', gridTemplateColumns:'1fr 1fr', gap:20 }}>
  <AuthCard onSuccess={()=>navigate('/')} />
  <div>
    <form onSubmit={onSearch} className="search-form">
    <input required placeholder="Search by city name" value={city} onChange={e=>setCity(e.target.value)} />
    <button type="submit">Search</button>
    </form>
    <p style={{ marginTop: 8, color:'#9fb0c7' }}>Search PGs by city after logging in or signing up.</p>
  </div>
</div>
</section>
<section>
<form onSubmit={onSearch} className="search-form" style={{ maxWidth: 600 }}>
<input required placeholder="Search by city name" value={city} onChange={e=>setCity(e.target.value)} />
<button type="submit">Search</button>
</form>
</section>
<section>
<h3>About</h3>
<p>PG Connect links seekers to verified PG owners. Admins can register properties and manage availability seamlessly.</p>
</section>
</main>
);
}
