import React from 'react';
import { Link } from 'react-router-dom';
export default function Header(){
const email = typeof window !== 'undefined' ? localStorage.getItem('email') : null;
const role = typeof window !== 'undefined' ? localStorage.getItem('role') : null;
const onLogout = ()=>{
localStorage.removeItem('token');
localStorage.removeItem('email');
localStorage.removeItem('role');
window.location.href = '/';
}
return (
<header className="header">
<div className="container">
<h1>PG Connect</h1>
<nav>
<Link className="nav-link" to="/">Home</Link>
{!email && <Link className="nav-link" to="/login">Login</Link>}
{!email && <Link className="nav-link" to="/signup">Signup</Link>}
{!email && <Link className="nav-link" to="/admin">Admin</Link>}
{email && <span className="nav-link" title={role || 'user'}>{email}</span>}
{email && role==='admin' && <Link className="nav-link" to="/admin/pg/new">Add PG</Link>}
{email && <button className="nav-link" onClick={onLogout} style={{ background: 'transparent', border: 0, cursor: 'pointer' }}>Logout</button>}
</nav>
</div>
</header>
);
}
