import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


export default function Home(){
const [city, setCity] = useState('');
const navigate = useNavigate();
const onSearch = (e)=>{
e.preventDefault();
// move to /search?city=name
navigate(`/search?city=${encodeURIComponent(city)}`);
}
return (
<main className="container">
<section className="hero">
<h2>Find PGs near you</h2>
<form onSubmit={onSearch} className="search-form">
<input required placeholder="Enter city name" value={city} onChange={e=>setCity(e.target.value)} />
<button type="submit">Search</button>
</form>
</section>
<section>
<h3>About</h3>
<p>PG Connect links seekers to PG owners. Login as admin to register your PG.</p>
</section>
</main>
);
}
